mapper.select:
SELECT empadd.cd_emp, emp.nm_krname, emp.job_ok
FROM empadd
INNER JOIN (
SELECT cd_emp, nm_krname, job_ok
FROM EMP
WHERE JOB_OK = #{jobOk}

    OR EXTRACT(YEAR FROM TO_DATE(DA_RETIRE, 'YYYY-MM-DD')) = #{refYear}

) emp ON empadd.cd_emp = emp.cd_emp

    ORDER BY empadd.CD_EMP


    ORDER BY emp.nm_krname

mapper.update:
UPDATE empadd
NM_CHNAME = #{nmChName}, NM_ENNAME = #{nmEnName}, YN_DRAWCONTRACTS = #{ynDrawContracts}, DC_MIDRETIRE = #{dcMidRetire}, FG_WEDDING = #{fgWedding}, FG_DISORDER = #{fgDisorder}, DA_BIRTH = #{daBirth}, STR_HOBBY = #{strHobby}, NM_SPECIAL = #{nmSpecial}, TY_EMPLOY = #{tyEmploy}, TY_RELIGION = #{tyReligion}, ZIP_HOME = #{zipHome}, ZIP_ORGIN = #{zipOrgin}, ADD_ORGIN1 = #{addOrgin1}, ADD_ORGIN2 = #{addOrgin2}, MN_KEEPGARDEN = #{mnKeepGarden}, MN_KEEPESTATE = #{mnKeepEstate}, FG_OWNHOUSE = #{fgOwnHouse}, TEL_OFFICE1 = #{telOffice1}, TEL_OFFICE2 = #{telOffice2}, TEL_OFFICE3 = #{telOffice3}, TEL_EXTENS = #{telExtens}, TY_MILIT = #{tyMilit}, WHY_MLTOFF = #{whyMltOff}, DA_MLTBEGIN = #{daMltBegin}, DA_MLTEND = #{daMltEnd}, FG_MLTTYPE = #{fgMltType}, NM_MLTBRANC = #{nmMltBranc}, STR_MLTDISCH = #{strMltDisch}, CD_MLTRANK = #{cdMltRank}, NO_SOCIAL = #{noSocial}, FG_SEX = #{fgSex}, CD_DEPT = #{cdDept}, CD_OFFDUTY = #{cdOffduty}, RANK_NO = #{rankNo}, DA_ENTER = #{daEnter}, DA_RETIRE = #{daRetire}, TEL_HOME1 = #{telHome1}, TEL_HOME2 = #{telHome2}, TEL_HOME3 = #{telHome3}, CEL_EMP1 = #{celEmp1}, CEL_EMP2 = #{celEmp2}, CEL_EMP3 = #{celEmp3},
WHERE CD_EMP = #{cdEmp}
