mapper.select:
SELECT d.ALLOW_YEAR, d."MONTH" AS "ALLOW_MONTH", d."DATE" AS "PAYMENT_DATE", s.YN_TAX , p.ALLOW_PAY, s.CD_ALLOW , s.NM_ALLOW , e.CD_EMP
    FROM SAALLOWPAY p LEFT OUTER JOIN SAALLOW s
        ON s.CD_ALLOW = p.CD_ALLOW LEFT OUTER JOIN EMP e
        ON e.CD_EMP = p.CD_EMP LEFT OUTER JOIN "DATE" d
        ON d.DATE_ID = p.DATE_ID
WHERE 1=1
    AND d.DATE_ID = #{dateId}
    AND e.CD_EMP = #{cdEmp}
 AND s.CD_ALLOW = #{cdAllow}

mapper.insert:
INSERT INTO SAALLOWPAY(
  CD_ALLOW , ALLOW_PAY , CD_EMP  , DATE_ID)
VALUES (
    #{cdAllow}
    , #{allowPay}
    , #{cdEmp}
    , #{dateId}
)

mapper.update:
UPDATE SAALLOWPAY
    SET ALLOW_PAY = #{allowPay}
WHERE CD_EMP = #{cdEmp}
    AND CD_ALLOW = #{cdAllow}
    AND DATE_ID = #{dateId}

mapper.insert:

    SELECT DATE_ID
    FROM "DATE"
    WHERE allow_year = #{allowYear}
    AND "MONTH" = #{allowMonth}
    AND "DATE" = #{paymentDate}



        INSERT INTO "DATE" (allow_year, "MONTH", "DATE", DATE_ID)
        VALUES (
            #{allowYear}
            , #{allowMonth}
            , #{paymentDate}
            , #{allowYear}#{allowMonth}#{paymentDate}
        )

            RETURNING DATE_ID INTO #{dateId}



INSERT INTO SAALLOWPAY(
    CD_ALLOW
    , ALLOW_PAY
    , CD_EMP
    , DATE_ID
) VALUES (
    #{cdAllow}
    , #{allowPay}
    , #{cdEmp}
    , #{dateId}
)

mapper.select:
SELECT "DATE" AS "paymentdate" ,  DATE_ID AS "dateId"
    FROM "DATE" d
WHERE d.ALLOW_YEAR = #{allowYear}
    AND d."MONTH" = #{allowMonth}
