mapper.select:
SELECT a.CD_ALLOW, a.NM_ALLOW, SUM(a.ALLOW_PAY) AS "ALLOW_PAY" FROM ( SELECT s.CD_ALLOW, s.NM_ALLOW, s.YN_TAX, p.ALLOW_PAY, p.CD_EMP FROM SAALLOW s LEFT OUTER JOIN ( SELECT CD_EMP, CD_ALLOW, YN_TAX, SUM(ALLOW_PAY) AS "ALLOW_PAY" FROM SAALLOWPAY WHERE DATE_ID = #{dateId} AND CD_EMP = #{cdEmp} AND SAL_DIVISION = #{salDivision} GROUP BY CD_ALLOW, YN_TAX, CD_EMP ) p ON p.CD_ALLOW = s.CD_ALLOW AND p.YN_TAX = s.YN_TAX WHERE 1=1
AND s.SAL_DIVISION = #{salDivision}
AND s.YN_USE = 'Y' UNION ALL SELECT s.CD_ALLOW, s.NM_ALLOW, s.YN_TAX, p.ALLOW_PAY, p.CD_EMP FROM SAALLOW s INNER JOIN ( SELECT CD_EMP, CD_ALLOW, YN_TAX, SUM(ALLOW_PAY) AS "ALLOW_PAY" FROM SAALLOWPAY WHERE DATE_ID = #{dateId} AND CD_EMP = #{cdEmp} AND SAL_DIVISION = #{salDivision} GROUP BY CD_ALLOW, YN_TAX, CD_EMP ) p ON p.CD_ALLOW = s.CD_ALLOW AND p.YN_TAX = s.YN_TAX WHERE 1=1
AND s.SAL_DIVISION = #{salDivision}
AND s.YN_USE = 'N' ) a GROUP BY a.CD_ALLOW, a.NM_ALLOW

SELECT DECODE(SUM(CASE WHEN p.YN_TAX = 'Y' THEN ALLOW_PAY ELSE '0' END), NULL, '0', SUM(CASE WHEN p.YN_TAX = 'Y' THEN ALLOW_PAY ELSE '0' END)) AS "sumByY", DECODE(SUM(CASE WHEN p.YN_TAX = 'N' THEN ALLOW_PAY ELSE '0' END), NULL, '0', SUM(CASE WHEN p.YN_TAX = 'N' THEN ALLOW_PAY ELSE '0' END)) AS "sumByN", DECODE(SUM(ALLOW_PAY), NULL, 0, SUM(ALLOW_PAY)) AS "sumAllowPay" FROM SAALLOWPAY p LEFT OUTER JOIN SAALLOW s ON s.CD_ALLOW = p.CD_ALLOW AND s.YN_TAX = p.YN_TAX WHERE 1=1
AND p.CD_EMP = #{cdEmp} AND DATE_ID = #{dateId}
AND p.SAL_DIVISION = #{salDivision}
AND p.YN_TAX IN ('Y', 'N')

SELECT a.CD_ALLOW, a.NM_ALLOW, SUM(a.ALLOW_PAY) AS "ALLOW_PAY", a.CD_EMP , a.SAL_DIVISION, a.DATE_ID , (SELECT "MONTH" FROM "DATE" WHERE DATE_ID = #{dateId}) AS "ALLOW_MONTH" FROM ( SELECT s.CD_ALLOW, s.NM_ALLOW, s.YN_TAX, p.ALLOW_PAY, p.CD_EMP, p.SAL_DIVISION, p.DATE_ID FROM SAALLOW s INNER JOIN ( SELECT CD_EMP, CD_ALLOW, YN_TAX, SUM(ALLOW_PAY) AS "ALLOW_PAY", SAL_DIVISION, DATE_ID FROM SAALLOWPAY WHERE DATE_ID = #{dateId} AND CD_EMP = #{cdEmp} GROUP BY CD_ALLOW, YN_TAX , CD_EMP, SAL_DIVISION, DATE_ID ) p ON p.CD_ALLOW = s.CD_ALLOW AND p.YN_TAX = s.YN_TAX
WHERE s.SAL_DIVISION = #{salDivision}
) a GROUP BY a.CD_ALLOW, a.NM_ALLOW , a.CD_EMP, a.SAL_DIVISION, a.DATE_ID

mapper.update:
MERGE INTO SAALLOWPAY target USING (
SELECT #{item.cdAllow} AS CD_ALLOW, #{item.cdEmp} AS CD_EMP, #{item.dateId} AS DATE_ID, #{item.allowPay} AS ALLOW_PAY, #{item.ynTax} AS YN_TAX, #{item.salDivision} AS SAL_DIVISION FROM dual
) source ON (target.CD_ALLOW = source.CD_ALLOW AND target.CD_EMP = source.CD_EMP AND target.DATE_ID = source.DATE_ID AND target.YN_TAX = source.YN_TAX AND target.SAL_DIVISION = source.SAL_DIVISION ) WHEN MATCHED THEN UPDATE SET target.ALLOW_PAY = source.ALLOW_PAY WHEN NOT MATCHED THEN INSERT (CD_ALLOW, CD_EMP, DATE_ID, ALLOW_PAY , YN_TAX , SAL_DIVISION) VALUES (source.CD_ALLOW, source.CD_EMP, source.DATE_ID, source.ALLOW_PAY , source.YN_TAX , source.SAL_DIVISION)

mapper.select:
SELECT a.CD_ALLOW AS "cdAllow" , a.YN_TAX AS "ynTax" , (SELECT s.NM_ALLOW FROM SAALLOW s WHERE s.CD_ALLOW = a.CD_ALLOW AND s.YN_TAX = a.YN_TAX) AS "nmAllow" , TO_CHAR(sum(p.ALLOW_PAY)) AS "sumAllowPay" FROM SAALLOW a LEFT OUTER JOIN SAALLOWPAY p ON a.CD_ALLOW = p.CD_ALLOW AND a.YN_TAX =p.YN_TAX LEFT OUTER JOIN "DATE" d ON p.DATE_ID = d.DATE_ID LEFT OUTER JOIN EMP e ON p.CD_EMP = e.CD_EMP WHERE 1=1
AND d."DATE" = #{paymentDate}
AND (e.DA_RETIRE IS NULL OR e.DA_RETIRE < #{paymentDate})
AND d."MONTH" = #{allowMonth}
AND (e.DA_RETIRE IS NULL OR TO_DATE(e.DA_RETIRE, 'YYYY-MM-DD') < TO_DATE( #{allowMonth}, 'YYYY-MM'))
AND d.ALLOW_YEAR = #{allowYear}
AND (e.DA_RETIRE IS NULL OR TO_DATE(e.DA_RETIRE, 'YYYY') < TO_DATE( #{allowYear}, 'YYYY'))
GROUP BY a.CD_ALLOW, a.YN_TAX ORDER BY "nmAllow"

mapper.delete:
DELETE FROM SAALLOWPAY
WHERE CD_ALLOW = #{cdAllow}
AND DATE_ID = #{dateId}
AND CD_EMP = #{cdEmp}
AND SAL_DIVISION = #{salDivision}

mapper.update:
UPDATE SAALLOWPAY
SET ALLOWPAY = #{allowPay}
WHERE CD_DEDUCT = #{cdDeduct}
AND DATE_ID = #{dateId}
AND CD_EMP = #{cdEmp}

mapper.select:
SELECT d."DATE" AS "paymentDate" , d.ALLOW_YEAR AS "allowYear" , d."MONTH" AS "allowMonth" , d.DATE_ID AS "dateId" , p.SAL_DIVISION AS "salDivision" , (SELECT CODE_NAME FROM CODE c WHERE CODE_ID = p.SAL_DIVISION) AS "salDivisionName" , DECODE(d.YN_COMPLETE,'Y','완료','') AS "ynComplete" FROM "DATE" d LEFT OUTER JOIN SAALLOWPAY p ON p.DATE_ID = d.DATE_ID LEFT OUTER JOIN SAALLOW s ON p.CD_ALLOW = s.CD_ALLOW WHERE 1=1 AND d.ALLOW_YEAR = #{allowYear}
AND d."MONTH" = #{allowMonth}
GROUP BY d."DATE", d.DATE_ID, d."MONTH", d.ALLOW_YEAR, p.SAL_DIVISION, d.YN_COMPLETE ORDER BY d."DATE"

SELECT SUM(p.ALLOW_PAY)
FROM SAALLOWPAY p LEFT OUTER JOIN "DATE" d
ON p.DATE_ID = d.DATE_ID
WHERE p.CD_ALLOW = #{cdAllow}
AND d."MONTH" = #{allowMonth}
AND p.CD_EMP = #{cdEmp}
AND p.SAL_DIVISION = #{salDivision}
GROUP BY p.CD_ALLOW

SELECT SUM(ALLOW_PAY)
FROM SAALLOWPAY
WHERE YN_TAX = #{ynTax}
AND DATE_ID = #{dateId}
AND CD_EMP = #{cdEmp}

SELECT COALESCE(CAST(SUM(CASE WHEN s.SAL_DIVISION = 'SAL' THEN ALLOW_PAY ELSE '0' END) AS INTEGER), 0) AS "sumTaxYBySal", COALESCE(CAST(SUM(CASE WHEN s.SAL_DIVISION = 'BONUS' THEN ALLOW_PAY ELSE '0' END) AS INTEGER), 0) AS "sumTaxYByBonus" FROM SAALLOWPAY p LEFT OUTER JOIN SAALLOW s ON s.CD_ALLOW= p.CD_ALLOW WHERE 1=1
AND p.CD_EMP = #{cdEmp} AND p.DATE_ID IN (SELECT DATE_ID FROM "DATE" WHERE "DATE" IN (SELECT "DATE" FROM "DATE" WHERE DATE_ID = #{dateId})) AND p.SAL_DIVISION = #{salDivision}
AND s.YN_TAX = 'Y'

mapper.update:
UPDATE SAALLOW
NM_ALLOW = #{nmAllow}, YN_TAX = #{ynTax}, SAL_DIVISION = #{salDivision}, COMMONLY_YN = #{commonlyYn}, MONTHLY_YN = #{monthlyYn}, NONTAX_LIMIT= #{nonTaxLimit}, YN_USE= #{ynUse},
WHERE CD_ALLOW = #{cdAllow} AND YN_TAX = #{originYnTax}

UPDATE SAALLOW
SET NONTAX_LIMIT = #{nontaxLimit}
WHERE CD_ALLOW = #{cdAllow} AND YN_TAX = 'N'

mapper.delete:
DELETE FROM SAALLOWPAY s
WHERE DATE_ID IN (SELECT date_id FROM "DATE" WHERE "MONTH" = #{allowMonth})
AND SAL_DIVISION =#{salDivision}
