mapper.select:
SELECT
    d.CD_DEDUCT
    , d.NM_DEDUCT
    , p.ALLOW_PAY
    , p.CD_EMP , p.DATE_ID
FROM SADEDUCT d LEFT OUTER JOIN SADEDUCTPAY p
    ON d.CD_DEDUCT = p.CD_DEDUCT
    AND p.DATE_ID = #{dateId}
    AND p.CD_EMP = #{cdEmp}
 AND s.CD_DEDUCT = #{cdDeduct}

mapper.update:
UPDATE SADEDUCTPAY
set ALLOW_PAY = #{allowPay}
WHERE CD_EMP = #{cdEmp}
AND DATE_ID = #{dateId}
AND CD_DEDUCT = #{cdDeduct}
