mapper.select:
SELECT
    d.CD_DEDUCT
    , d.NM_DEDUCT
    , p.ALLOW_PAY
    , p.CD_EMP , p.DATE_ID
FROM SADEDUCT d LEFT OUTER JOIN SADEDUCTPAY p
    ON d.CD_DEDUCT = p.CD_DEDUCT
    AND p.DATE_ID = #{dateId}
    AND p.CD_EMP = #{cdEmp}
 AND s.CD_DEDUCT = #{cdDeduct}

SELECT d.CD_DEDUCT AS "cdDeduct" , (SELECT s.NM_DEDUCT FROM SADEDUCT s WHERE s.CD_DEDUCT = d.CD_DEDUCT) AS "nmDeduct" , TO_CHAR(sum(p.ALLOW_PAY)) AS "sumDeductPay" FROM SADEDUCT d LEFT OUTER JOIN SADEDUCTPAY p ON p.CD_DEDUCT = d.CD_DEDUCT LEFT OUTER JOIN "DATE" d ON p.DATE_ID = d.DATE_ID LEFT OUTER JOIN EMP e ON e.CD_EMP = p.CD_EMP
AND D."DATE" = #{paymentDate}
AND e.DA_RETIRE < #{paymentDate}
AND d."MONTH" = #{allowMonth}
AND TO_DATE(e.DA_RETIRE, 'YYYY-MM-DD') < TO_DATE( #{allowMonth}, 'YYYY-MM')
AND d.ALLOW_YEAR = #{allowYear}
AND TO_DATE(e.DA_RETIRE, 'YYYY') < TO_DATE( #{allowYear}, 'YYYY')
GROUP BY d.CD_DEDUCT

mapper.update:
MERGE INTO SADEDUCTPAY target USING (
SELECT #{item.cdDeduct} AS CD_DEDUCT, #{item.cdEmp} AS CD_EMP, #{item.dateId} AS DATE_ID, #{item.allowPay} AS ALLOW_PAY FROM dual
) source ON (target.CD_DEDUCT = source.CD_DEDUCT AND target.CD_EMP = source.CD_EMP AND target.DATE_ID = source.DATE_ID) WHEN MATCHED THEN UPDATE SET target.ALLOW_PAY = source.ALLOW_PAY WHEN NOT MATCHED THEN INSERT (CD_DEDUCT, CD_EMP, DATE_ID, ALLOW_PAY) VALUES (source.CD_DEDUCT, source.CD_EMP, source.DATE_ID, source.ALLOW_PAY)
