mapper.select:
SELECT i.CD_EMP
    , (SELECT NM_KRNAME FROM EMP e WHERE e.CD_EMP = i.CD_EMP) as NM_EMP
    , i.RANK_NO
    , i.MN_REDUCTION
FROM SAEMPINFO i
WHERE i.CD_EMP IN (
    SELECT DISTINCT p.CD_EMP
        FROM SAALLOW s , SAALLOWPAY p
    WHERE 1=1
    AND s.CD_ALLOW = p.CD_ALLOW
    AND s.SAL_DIVISION = #{salDivision}
    AND p.ALLOW_MONTH = #{allowMonth}
    AND p.PAYMENT_DATE = #{paymentDate}
)
   AND i.CD_EMP       = #{searchCdEmp}      
   AND i.CD_DEPT      = #{searchCdDept}     
   AND i.RANK_NO      = #{searchRankNo}     
   AND i.CD_OCCUP     = #{searchCdOccup}    
   AND i.CD_FIELD     = #{searchCdField}    
   AND i.CD_PROJECT   = #{searchCdProject}  
   AND i.YN_UNIT      = #{searchYnUnit}     
   AND i.YN_FORLABOR  = #{searchYnForlabor}

mapper.update:
UPDATE SAEMPINFO
SET CD_EMP = #{cdEmp}
    , MN_1_1_1 = #{mn111}
    , DA_ENTER = #{daEnter}
    , YN_MATEDED = #{ynMateDed}
    , NUM_20FAMILY = #{num20Family}
    , NUM_60FAMILY = #{num60Family}
    , NUM_MANYFAMILY = #{numManyFamily}
    , YN_RESIDENT = #{ynResident}
    , YN_UNIT = #{ynUnit}
    , YN_FORLABOR = #{ynForLabor}
    , YN_OVERWORK = #{ynOverwork}
    , CD_OCCUP = #{cdOccup}
    , RANK_NO = #{rankNo}
    , CD_DEPT = #{cdDept}
    , CD_FIELD = #{cdField}
    , CD_PROJECT = #{cdProject}
    , AM_NATIONPEN = #{amNationPen}
    , AM_HEALTHINSUR = #{amHealthInsur}
    , MN_2_1_1 = #{mn211}
    , NO_SOCIAL = #{noSocial}
WHERE CD_EMP = #{cdEmp}
